package com.team1.util;


public class HanbitUserDTO {

	private String name, gender;
	private int hb_code;
	
	public HanbitUserDTO() {
		// TODO Auto-generated constructor stub
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getGender() {
		return gender;
	}

	public void setGender(String gender) {
		this.gender = gender;
	}

	public int getHb_code() {
		return hb_code;
	}

	public void setHb_code(int hb_code) {
		this.hb_code = hb_code;
	}
	
}
